#include "common.h"

#define N    (7)
#define L    (3)
#define S    (512/8)

/*
 * Prime source is:
 * https://primes.utm.edu/lists/small/small3.html
 */
static char prime_str[] = "";

int main()
{
    int exitcode = EXIT_FAILURE;
    mpz_t prime;
    mpz_init_set_str(prime, prime_str, 10);
    exitcode = do_test(prime, N, L, S);
    mpz_clear(prime);

    return exitcode;
}
